import React, { useContext } from "react";
import { Container,Card, Navbar ,Row , Col, Button, Overlay } from "react-bootstrap";
import classes from './Cart.module.css';
import Modal from "../Modal/Modal";
import CartContext from "../../store/cart-context";
import CartItem from "./CartItem";




    
  const Cart = (props) =>{
    const cartCtx =useContext(CartContext);
    const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`
    const hasItems = cartCtx.items.length > 0 ;

        
        
    return (
        <Modal >
            <Container className={classes.container} fluid>
            
                <Row>
                    <Col >
                        <h1>CART</h1>
                        <>------------------------------------------------------</>
                    </Col>
                    <Col>
                        <Button className={classes.cancel} variant="danger" onClick={props.closeCart}>X</Button>
                    </Col>
                </Row>
                
                <Row>
                    <Col sm={4}>
                        <h3>Item</h3>
                        <>_____________</>
                        
                    </Col>
                    <Col sm={3}>
                        <h3 >PRICE</h3>
                        <>_____________</>
                        
                    </Col>
                    <Col sm={3}>
                        <h3>
                            QTY
                        </h3>
                        <>_____________</>
                        
                    </Col>
                </Row>    
                {cartCtx.items.map((product =>{
                    return(<CartItem quantity={product.amount} price={product.price} title={product.title}/>)
                }))
                }
                <Row>
                    <Col  align='right'>
                    TOTAL
                    </Col>
                    <br />
                    <Col>
                    {totalAmount}
                    </Col>
                </Row>
               
                        
                {hasItems && <Button className={classes.purchasebtn} variant='info'>PURCHASE</Button>}
                    
            </Container>
        </Modal>
        
    )
  };
  
export default Cart;
