import React from "react";
import { Button, Card,Row, Col,CardImg, Container } from "react-bootstrap";
import CardHeader from "react-bootstrap/esm/CardHeader";

import './App.css';
import Header from "./components/Header/Header";
import CartModal from "./components/Cart/CartItems";




const  App = () => {
  const productsArr = [

    {

    title: 'Colors',

    price: 100,

    imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%201.png',

    },

    {

    title: 'Black and white Colors',

    price: 50,

    imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%202.png',

    },

    {

    title: 'Yellow and Black Colors',

    price: 70,

    imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%203.png',

    },

    {

    title: 'Blue Color',

    price: 100,

    imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%204.png',

    }

    ]
    const showCart = () =>{
      return <CartModal/>
    }
  return (
    <div>
      <Header/>
      <Container fluid>
        <Card>
          <h1>
            The Generics
          </h1>
        </Card>
      </Container>

      <Container className="products-page">    
      {productsArr.map((product =>{
      // return (
      return (

          <Row >
            <Col >
                <Card className='shadow-lg' style={{ width: '10rem' }}>


                    <Card.Body>
                      <CardHeader>{product.title}</CardHeader>
                      <CardImg src={product.imageUrl}/>
                      <Card.Footer>
                          ${product.price}
                          <Button variant="success" onClick={showCart}>Add To Cart</Button>
                      </Card.Footer>

                    </Card.Body>
                </Card>
              </Col>
            </Row>



      )}))

    }
    </Container> 
    {/* <CartModal/> */}

    </div>
  );
}

export default App;
