import React, { useState } from "react";
import { Button, Card,Row, Col,CardImg, Container ,Navbar} from "react-bootstrap";
import CardHeader from "react-bootstrap/esm/CardHeader";

import './App.css';
import Header from "./components/Header/Header";
import CartModal from "./components/Cart/CartItems";
import AvailableProducts from "./components/AvailableItems/AvailableProducts";
import CartProvider from "./store/CartProvider";
import CartContext from "./store/cart-context";




const  App = (props) => {
  const [cartState , setCartState ] = useState(false);
  const showCart = ()=>{
    setCartState(true);

  }
  const closeCart = () =>{
    setCartState(false);
  }
  const addToCartHandler = amount =>{
    CartContext.addIem({
            id : props.id,
            name : props.name,
            amount: amount,
            price : props.price
    })
  }


  if (cartState){
    return (<CartModal closeCart={closeCart}/>)
  }  
  else{
    return (

      <CartProvider>
        <Header showCart={showCart} closeCart={closeCart} />
        <Navbar>
        <Container fluid  style={{backgroundColor: 'pink'}}>

          <h1 className="p-3" >
            The Generics
          </h1>

      </Container>
        </Navbar>

        <Container className="products-page"> 
          <Row >   
              <AvailableProducts onAddToCart={addToCartHandler}/>
          </Row>


      </Container> 
      {/* { cartState && <CartModal closeCart={closeCart}/> } */}
      {<pre>{props.amount}</pre>} 
      </CartProvider>

    )}
}

export default App;
