import React from "react";
import { Container,Card, Navbar ,Row , Col, Button, Overlay } from "react-bootstrap";
import { propTypes } from "react-bootstrap/esm/Image";




    
  const CartModal = (props) =>{
    const cartElements = [

        {
        
        title: 'Colors',
        
        price: 100,
        
        imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%201.png',
        
        quantity: 2,
        
        },
        
        {
        
        title: 'Black and white Colors',
        
        price: 50,
        
        imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%202.png',
        
        quantity: 3,
        
        },
        
        {
        
        title: 'Yellow and Black Colors',
        
        price: 70,
        
        imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%203.png',
        
        quantity: 1,
        
        }
        
        ]
    return (<Overlay target={target.current} show={show} placement="right">
    {({ placement, arrowProps, show: _show, popper, ...props }) => (
      <div
        {...props}
        style={{
          position: 'absolute',
          backgroundColor: 'rgba(255, 100, 100, 0.85)',
          padding: '2px 10px',
          color: 'white',
          borderRadius: 3,
          ...props.style,
        }}
      >
        <Container md>
            <header>
                <h1>Cart</h1>
            </header>
            <Button variant="danger" onClick={props.closeCart}>close</Button>
            <Row>
                <Col sm={3}>
                    <h2>Item</h2>
                    
                </Col>
                <Col sm={3}>
                    <h2>PRICE</h2>
                    
                </Col>
                <Col sm={3}>
                    <h2>
                        QUANTITY
                    </h2>
                    
                </Col>
            </Row>    
            {cartElements.map((product =>{
                return(<Row>
                <Col sm={3}>
                    <ul>{product.title}</ul>
                </Col>
                <Col sm={3}>
                    <ul>{product.price}</ul>
                </Col>
                <Col sm={3}>
                
                    <ul>
                        {product.quantity}
                    </ul>
                </Col>
                <Col>    
                    <Button variant='danger' >REMOVE</Button>
                </Col>
            </Row>)
            }))
            }
            <div>
                TOTAL
            </div>
            <Button variant='info' size='sm'>PURCHASE</Button>{''}
        </Container>

      </div>
    )}
  </Overlay>
    
        

        
    )
  };
  
export default CartModal;
