import React ,{useRef,useState} from 'react';
import { Button, Card,Row, Col,CardImg, Container ,Navbar} from "react-bootstrap";
import CardHeader from "react-bootstrap/esm/CardHeader";
import classes from './Availableproducts.module.css';
import Input from '../../Input/input';

const AvailableProducts = (props) =>{
    const productsArr = [

        {
        
        title: 'Colors',
        
        price: 100,
        
        imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%201.png',
        
        },
        
        {
        
        title: 'Black and white Colors',
        
        price: 50,
        
        imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%202.png',
        
        },
        
        {
        
        title: 'Yellow and Black Colors',
        
        price: 70,
        
        imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%203.png',
        
        },
        
        {
        
        title: 'Blue Color',
        
        price: 100,
        
        imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%204.png',
        
        }
        
        ]
        const [amounIsValid , setAmountIsValid ] = useState(true);
    // const cartCtx = useContext(CartContext);
        const amountInputRef = useRef();
        const addItemToCart = (event) =>{
            event.preventDefault();
            
            const enteredAmount = amountInputRef.current.value;
            const enteredAmountNumber = +enteredAmount;
            console.log(enteredAmount)

        if(enteredAmount.trim().length === 0 || enteredAmountNumber < 1 ||
        enteredAmountNumber > 5){
            setAmountIsValid(false);
            return;
        }

        props.onAddToCart(enteredAmountNumber);
        }
        
    return (
        <Row>
        {productsArr.map((product =>{
        // return (
        return (
          
            
              <Col >
                  <Card className='shadow-lg' style={{ width: '10rem' }}>
                
                      
                      <Card.Body>
                        <CardHeader>{product.title}</CardHeader>
                        <CardImg src={product.imageUrl}/>
                         <form className={classes.form} >
                            <Input 
                                ref={amountInputRef}
                                label='Amount' 
                                input={{
                                id:'id',
                                type:'number',
                                min:'1',
                                max:'5',
                                step:'1',
                                defaultValue:'1'

                            }}/>
                            <button 
                              onClick={addItemToCart}>Add To Cart</button>
                            {!amounIsValid && <p>Please enter a valid amount between (1-5)</p>}
                        </form>
                        <Card.Footer>
                            ${product.price}
                            
                        </Card.Footer>
                        
                      </Card.Body>
                  </Card>
                </Col>
             
        )}))
        
        }
        </Row>);
};

export default AvailableProducts;
